-- ==========================================
-- ส่วนที่ 1: สร้างตาราง (CREATE TABLE)
-- ==========================================

CREATE TABLE IF NOT EXISTS major (
    id VARCHAR(50) PRIMARY KEY,
    name VARCHAR(255),
    level VARCHAR(50)
);

CREATE TABLE IF NOT EXISTS teacher (
    id VARCHAR(50) PRIMARY KEY,
    name VARCHAR(255),
    phone_number VARCHAR(50)
);

CREATE TABLE IF NOT EXISTS class (
    id VARCHAR(50) PRIMARY KEY,
    major_id VARCHAR(50),
    year VARCHAR(10),
    class_number VARCHAR(10),
    class_name VARCHAR(255),
    FOREIGN KEY (major_id) REFERENCES major(id)
);

CREATE TABLE IF NOT EXISTS teacher_major (
    teacher_id VARCHAR(50),
    major_id VARCHAR(50),
    FOREIGN KEY (teacher_id) REFERENCES teacher(id),
    FOREIGN KEY (major_id) REFERENCES major(id)
);

CREATE TABLE IF NOT EXISTS student (
    id VARCHAR(50) PRIMARY KEY,
    name VARCHAR(255),
    citizen_id VARCHAR(50),
    role VARCHAR(50),
    class_id VARCHAR(50),
    FOREIGN KEY (class_id) REFERENCES class(id)
);

CREATE TABLE IF NOT EXISTS user_account (
    id VARCHAR(50) PRIMARY KEY,
    username VARCHAR(100),
    citizen_id_hash VARCHAR(255),
    role VARCHAR(50),
    ref_id VARCHAR(50) 
);

CREATE TABLE IF NOT EXISTS activity (
    id VARCHAR(50) PRIMARY KEY,
    name VARCHAR(255),
    start_time DATETIME,
    end_time DATETIME,
    for_student BOOLEAN,
    for_leader BOOLEAN,
    for_teacher BOOLEAN,
    is_recurring INT,           -- ✅ ปรับเป็น INT เรียบร้อย
    created_by VARCHAR(50),
    class_id VARCHAR(50),       -- ✅ คงไว้เป็น VARCHAR (สัมพันธ์กับ class.id)
    major_id VARCHAR(50),
    activity_type VARCHAR(50),
    FOREIGN KEY (class_id) REFERENCES class(id),
    FOREIGN KEY (major_id) REFERENCES major(id)
);

CREATE TABLE IF NOT EXISTS activity_check (
    id VARCHAR(50) PRIMARY KEY,
    activity_id VARCHAR(50),
    student_id VARCHAR(50),
    status VARCHAR(50),
    date DATE,
    semester VARCHAR(10),
    academic_year VARCHAR(10),
    FOREIGN KEY (activity_id) REFERENCES activity(id),
    FOREIGN KEY (student_id) REFERENCES student(id)
);

CREATE TABLE IF NOT EXISTS term_score (
    id VARCHAR(50) PRIMARY KEY,
    student_id VARCHAR(50),
    major_id VARCHAR(50),
    semester VARCHAR(10),
    academic_year VARCHAR(10),
    flag_ceremony_percentage DECIMAL(5,2),
    department_activity_percentage DECIMAL(5,2),
    total_score DECIMAL(5,2),
    is_passed BOOLEAN,
    last_updated DATETIME,
    FOREIGN KEY (student_id) REFERENCES student(id),
    FOREIGN KEY (major_id) REFERENCES major(id)
);


